mapper.select:
		select t.id,t.name,t.address,t.zipcode,t.fax from customer t where 1=1

            order by #{sort} #{order}


            limit #{start},#{end}

mapper.update:
	update customer

		name=#{name},
        address=#{address},
        zipcode=#{zipcode},
        fax=#{fax}

	where id = #{id}
